package basics;

public class ArthimeticOperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=25;
		int num2=10;
		System.out.println(num1+num2);
		System.out.println(num1-num2);
		System.out.println(num1*num2);
		System.out.println(num1/num2);
		System.out.println(num1%num2); //this gives you reminder..
		System.out.println((double)num1/num2); //this gives your right percentile. double is for typecasting the result.
	}

}

