package basics;

public class PreAndPostDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10;
		int num2=5;
//		System.out.println(num1++);//this is post increment. to perform increment value later.
//		System.out.println(num1);
//		
//		System.out.println(num2--);//this is post decrement. to perform decrement value later.
//		System.out.println(num2);
		
		System.out.println(++num1);//this is Pre increment. to perform increment value before.
		System.out.println(num1);
		
		System.out.println(--num2);//this is pre decrement. to perform decrement value before.
		System.out.println(num2);
	}

}
